using System;
using System.IO;

namespace ClearCanvas.ImageServer.Services.Streaming.ImageStreaming
{
    /// <summary>
    /// Represents a response generated by processors that implement <see cref="IWADORequestTypeHandler"/>
    /// </summary>
    public class WADOResponse : IDisposable
    {
        #region Private Members
        private string _contentType;
        private byte[] _output;
        private bool _isLast;
        #endregion

        #region Public Properties

        /// <summary>
        /// Gets the content type
        /// </summary>
        public string ContentType
        {
            get { return _contentType; }
            set { _contentType = value; }
        }

        /// <summary>
        /// Gets the data stream that will be sent to the client
        /// </summary>
        public byte[] Output
        {
            get { return _output; }
            set { _output = value; }
        }

        public bool IsLast
        {
            get { return _isLast; }
            set { _isLast = value; }
        }

        #endregion


        #region IDisposable Members

        public void Dispose()
        {
        }

        #endregion
    }
}
